option.value:
const fs = require('fs');
const path = require('path');
const file = path.resolve(__dirname, '..', 'components', 'Footer.tsx');
let src = fs.readFileSync(file, 'utf8');

// Ersetzungen: Light/Dark-Mix -> feste Dark-Standards
src = src.replace(/text-\[#2a2520\]\s+dark:text-\[#f5f1ed\]/g, 'text-[#f5f1ed]');
src = src.replace(/text-\[#6b635a\]\s+dark:text-\[#b8aea5\]/g, 'text-[#b8aea5]');
src = src.replace(/hover:text-\[#d97757\]\s+dark:hover:text-\[#e89a7a\]/g, 'hover:text-[#e89a7a]');
src = src.replace(/border-\[#d97757\]\/10\s+dark:border-\[#e89a7a\]\/10/g, 'border-[#e89a7a]/10');

// Falls noch einzelne dark:-Reste vorhanden sind (Sicherheitsnetz):
src = src.replace(/\sdark:[^\s"]+/g, '');

fs.writeFileSync(file, src, 'utf8');
console.log('Footer.tsx dark:-Varianten entfernt und Dark als Standard gesetzt.');
